curl -X POST "https://trexity.app/api/v2/addresses/check" \
  -H "Content-Type: application/json" \
  -H "Authorization: Bearer YOUR_API_KEY" \
  -d '{
    "delivery": {
      "addressLine1": "456 Delivery St",
      "city": "Ottawa",
      "provinceCode": "ON",
      "postalCode": "K1A 0A6",
      "countryCode": "CA"
    },
    "pickup": {
      "addressLine1": "123 Pickup Ave",
      "city": "Ottawa",
      "provinceCode": "ON", 
      "postalCode": "K2G 6F2",
      "countryCode": "CA"
    }
  }'

curl -X POST "https://trexity.app/api/v2/rates" \
  -H "Content-Type: application/json" \
  -H "Authorization: Bearer YOUR_API_KEY" \
  -d '{
    "deliveryAddress": {
      "addressLine1": "456 Delivery St",
      "city": "Ottawa",
      "provinceCode": "ON",
      "postalCode": "K1A 0A6",
      "countryCode": "CA"
    },
    "pickupAddress": {
      "addressLine1": "123 Pickup Ave",
      "city": "Ottawa", 
      "provinceCode": "ON",
      "postalCode": "K2G 6F2",
      "countryCode": "CA"
    },
    "numLabels": 1
  }'

curl -X POST "https://trexity.app/api/v2/batching-operations" \
  -H "Content-Type: application/json" \
  -H "Authorization: Bearer YOUR_API_KEY" \
  -d '{
    "pickupAddress": {
      "addressLine1": "123 Warehouse St",
      "city": "Ottawa",
      "provinceCode": "ON",
      "postalCode": "K2G 6F2",
      "countryCode": "CA"
    },
    "shipments": [
      {
        "orderId": "order-001",
        "deliveryAddress": {
          "addressLine1": "456 Customer Ave",
          "city": "Ottawa",
          "provinceCode": "ON",
          "postalCode": "K1A 0A6",
          "countryCode": "CA"
        },
        "deliveryName": "John Doe",
        "deliveryEmail": "john.doe@example.com",
        "deliveryPhone": "+1-613-555-0123"
      }
    ]
  }'

curl -X POST "https://trexity.app/api/v2/locations/find" \
  -H "Content-Type: application/json" \
  -H "Authorization: Bearer YOUR_API_KEY" \
  -d '{
    "address": {
      "addressLine1": "123 Store St",
      "city": "Ottawa",
      "provinceCode": "ON",
      "postalCode": "K2G 6F2",
      "countryCode": "CA"
    },
    "name": "MyStore in Nepean"
  }'
